    LengthVar = min(String-&gt;Length, 16);                                   \
    if (PointerToOffsetCrossesPageBoundary(String-&gt;Buffer, 16)) {          \
        __movsb(Slot.Char, String-&gt;Buffer, LengthVar);                     \
